package com.xiaopengwei.springboot.admin.client;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;/** * <p> * admin - client 启动类 * </p> * * @author XiaoPengwei.com * @since 2019-04-17 */@SpringBootApplicationpublic class AdminClientApplication {    /**     * 若修改 server 的端口，需要在 properties 中配置     * 当此客户端停止时，server 端会自动给 server 端配置的邮箱发送邮件     */    public static void main(String[] args) {        SpringApplication.run(AdminClientApplication.class, args);        System.out.println("admin 控制台在：http://127.0.0.1:8080/#/");    }}