package com.xiaopengwei.springboot.asynchronous.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;/** * <p> * 用户实例类 * </p> * * @author XiaoPengwei.com * @since 2019-04-20 */@JsonIgnoreProperties(ignoreUnknown=true)public class User {    private String name;    private String blog;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getBlog() {        return blog;    }    public void setBlog(String blog) {        this.blog = blog;    }    @Override    public String toString() {        return "User [name=" + name + ", blog=" + blog + "]";    }}