package com.xiaopengwei.springboot.cache;import com.xiaopengwei.springboot.cache.repository.BookRepository;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.boot.CommandLineRunner;import org.springframework.stereotype.Component;/** * <p> * 执行程序 * </p> * * @author github.com/xpwi * @since 2019-04-20 */@Componentpublic class AppRunner implements CommandLineRunner {    private static final Logger logger = LoggerFactory.getLogger(AppRunner.class);    private final BookRepository bookRepository;    public AppRunner(BookRepository bookRepository) {        this.bookRepository = bookRepository;    }    @Override    public void run(String... args) throws Exception {        logger.info(".... Fetching books");        logger.info("isbn-1234 -->" + bookRepository.getByIsbn("isbn-1234"));        logger.info("isbn-4567 -->" + bookRepository.getByIsbn("isbn-4567"));        logger.info("isbn-1234 -->" + bookRepository.getByIsbn("isbn-1234"));        logger.info("isbn-4567 -->" + bookRepository.getByIsbn("isbn-4567"));        logger.info("isbn-1234 -->" + bookRepository.getByIsbn("isbn-1234"));        logger.info("isbn-1234 -->" + bookRepository.getByIsbn("isbn-1234"));    }}