package com.xiaopengwei.springboot.jdbc.repository.impl;import com.xiaopengwei.springboot.jdbc.entity.UserLogin;import com.xiaopengwei.springboot.jdbc.repository.IUserLoginRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.jdbc.core.JdbcTemplate;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Map;/** * <p> * JDBC Template 实现类 * </p> * * @author XiaoPengwei.com * @since 2019-04-19 */@Repositorypublic class UserLoginRepositoryImpl implements IUserLoginRepository {    @Autowired    private JdbcTemplate jdbcTemplate;    @Override    public int insert(UserLogin userLogin) {        String sql = "insert into user_login(id, user_name, password, email, phone, register_time) values(?, ?, ?, ?, ?, now())";        int insert = jdbcTemplate.update(sql,                userLogin.getId(),                userLogin.getUserName(),                userLogin.getPassword(),                userLogin.getEmail(),                userLogin.getPhone());        return insert;    }    @Override    public List<Map<String, Object>> findUserList() {        String sql = "select * from user_login";        return jdbcTemplate.queryForList(sql);    }}