package com.xiaopengwei.springboot.jdbc.service.impl;import com.xiaopengwei.springboot.jdbc.entity.UserLogin;import com.xiaopengwei.springboot.jdbc.repository.IUserLoginRepository;import com.xiaopengwei.springboot.jdbc.service.IUserLoginService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * <p> * 用户详情业务逻辑层实现 * </p> * * @author github.com/xpwi * @since 2019-04-18 */@Servicepublic class UserLoginServiceImpl implements IUserLoginService {    @Autowired    private IUserLoginRepository userLoginRepository;    @Override    public int insert(UserLogin userLogin) {        return userLoginRepository.insert(userLogin);    }    @Override    public List<Map<String, Object>> findUserList() {        return userLoginRepository.findUserList();    }}