package com.xiaopengwei.springboot.jpa.controller;import com.xiaopengwei.springboot.jpa.entity.UserLogin;import com.xiaopengwei.springboot.jpa.service.IUserLoginService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;/** * <p> * 用户登录注册 * </p> * * @author github.com/xpwi * @since 2019-04-19 */@RestController@RequestMapping("/user")public class UserLoginController {    @Autowired    private IUserLoginService userLoginServiceImpl;    /**     * 查找所有用户     *     * @return java.util.List<com.xiaopengwei.springboot.jpa.entity.UserLogin>     * @author github.com/xpwi     */    @GetMapping("/list")    public List<UserLogin> getAllUser() {        return userLoginServiceImpl.findAll();    }    /**     * 根据 ID 获取用户名称     *     * @param id     * @return java.lang.String     * @author github.com/xpwi     */    @GetMapping("/{id}")    public String getUserNameById(@PathVariable("id") int id) {        return userLoginServiceImpl.getUserNameById(id);    }}