package com.xiaopengwei.springboot.mongodb.repository;import com.xiaopengwei.springboot.mongodb.entity.Customer;import org.springframework.data.mongodb.repository.MongoRepository;import java.util.List;/** * <p> * 数据访问 * </p> * * @author XiaoPengwei.com * @since 2019-04-20 */public interface CustomerRepository extends MongoRepository<Customer, String> {    Customer findByFirstName(String firstName);    List<Customer> findByLastName(String lastName);}