package com.xiaopengwei.springboot.mybatis.controller;import com.xiaopengwei.springboot.mybatis.entity.StudentEO;import com.xiaopengwei.springboot.mybatis.entityvo.ReturnMessage;import com.xiaopengwei.springboot.mybatis.service.IStudentEOService;import io.swagger.annotations.ApiOperation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;import java.util.ArrayList;import java.util.List;/** * <p> * 测试 前端控制器 * </p> * * @author github.com/xpwi * @since 2019-04-08 */@RestControllerpublic class StudentEOController {    @Autowired    private IStudentEOService studentEOServiceImpl;    /**     * 根据 ID 获取用户信息     * 测试：/getStudentById.do?studentId=1     *     * @param studentId     * @return com.xiaopengwei.ssm.entityvo.ReturnMessage     * @author github.com/xpwi     */    @ApiOperation(value = "根据ID查询学生")    @GetMapping("/getStudentById.do")    public ReturnMessage getStudentById(int studentId) {        ReturnMessage returnMessage = new ReturnMessage();        returnMessage.setState("0");        if (studentId < 0) {            returnMessage.setMessage("该Id不存在！");        } else {            StudentEO studentEOById = studentEOServiceImpl.getStudentByStudentId(studentId);            if (studentEOById != null) {                ArrayList<StudentEO> studentEOList = new ArrayList<>();                studentEOList.add(studentEOById);                returnMessage.setData(studentEOList);                returnMessage.setState("1");                returnMessage.setMessage("查询成功！");            } else {                returnMessage.setState("2");                returnMessage.setMessage("该Id不存在！");            }        }        return returnMessage;    }    /**     * 查询所有学生     * 测试：/selectAllStudent.do     *     * @return com.xiaopengwei.ssm.entityvo.ReturnMessage     * @author github.com/xpwi     */    @ApiOperation(value = "查询所有学生")    @GetMapping("/selectAllStudent.do")    public ReturnMessage selectAllStudent() {        ReturnMessage returnMessage = new ReturnMessage();        returnMessage.setState("0");        List<StudentEO> studentEOList = studentEOServiceImpl.selectAll();        if (studentEOList.size() > 0) {            returnMessage.setData(studentEOList);            returnMessage.setState("1");            returnMessage.setMessage("查询成功！");        } else {            returnMessage.setState("2");            returnMessage.setMessage("没有数据！");        }        return returnMessage;    }    /**     * 创建新的学生     * 测试：/createStudent.do     *     * @return com.xiaopengwei.ssm.entityvo.ReturnMessage     * @author github.com/xpwi     */    @ApiOperation(value = "创建新的学生")    @GetMapping("/createStudent.do")    public ReturnMessage createStudent(StudentEO studentEO) {        ReturnMessage returnMessage = new ReturnMessage();        returnMessage.setState("0");        int insert = studentEOServiceImpl.createStudent(studentEO);        if (insert > 0) {            returnMessage.setState("1");            returnMessage.setMessage("创建成功！");        } else {            returnMessage.setState("2");            returnMessage.setMessage("创建失败！");        }        return returnMessage;    }}