package com.xiaopengwei.springboot.mybatis.service.impl;import com.xiaopengwei.springboot.mybatis.entity.StudentEO;import com.xiaopengwei.springboot.mybatis.mapper.StudentEOMapper;import com.xiaopengwei.springboot.mybatis.service.IStudentEOService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * <p> * 业务逻辑实现 * </p> * * @author github.com/xpwi * @since 2019-04-12 */@Servicepublic class StudentEOServiceImpl implements IStudentEOService {    @Autowired(required=false)    StudentEOMapper studentEOMapper;    @Override    public StudentEO getStudentByStudentId(int studetnId) {        return studentEOMapper.selectByPrimaryKey(studetnId);    }    @Override    public int createStudent(StudentEO studentEO) {        return studentEOMapper.insert(studentEO);    }    @Override    public List<StudentEO> selectAll() {        return studentEOMapper.selectAll();    }}