package com.xiaopengwei.springboot.multi.sources.config;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.PropertySource;/** * <p> * 用户信息 * </p> * * @author XiaoPengwei.com * @since 2019-04-19 */@Configuration@PropertySource(value = "classpath:user.properties")@ConfigurationProperties(prefix = "com.xiaopengwei.springboot")public class UserConfig {    private String name;    private String email;    private String home;    private String greeting;    public String getGreeting() {        return greeting;    }    public void setGreeting(String greeting) {        this.greeting = greeting;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getHome() {        return home;    }    public void setHome(String home) {        this.home = home;    }}