package com.xiaopengwei.springboot.mybatis.controller;import com.xiaopengwei.springboot.multi.sources.config.UserDevProperties;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * <p> * dev 中的配置 * </p> * * @author github.com/xpwi * @since 2019-04-19 */@RestControllerpublic class TestDevConfigController {    @Autowired    private UserDevProperties userDevProperties;    @RequestMapping(value = "/userDevProperties")    public String userDevProperties(){        return "userDevProperties: "+"\n"+userDevProperties.getGreeting() +                "\n>>>>"+userDevProperties.getName()+                "\n>>>>"+userDevProperties.getAge()+                "\n>>>>"+userDevProperties.getHome();    }}