package com.xiaopengwei.springboot.rabbitmq.rabbit.famout;import org.springframework.amqp.rabbit.annotation.RabbitHandler;import org.springframework.amqp.rabbit.annotation.RabbitListener;import org.springframework.stereotype.Component;/** * <p> *消费者 * </p> * * @author XiaoPengwei.com * @since 2019-04-20 */@Component@RabbitListener(queues = "fanout.B")public class FanoutReceiverB {    @RabbitHandler    public void process(String message) {        System.out.println("fanout Receiver B  : " + message);    }}