package com.xiaopengwei.springboot.schedule;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.scheduling.annotation.EnableScheduling;/** * <p> * 定时任务 - 启动类 * </p> * * @author XiaoPengwei.com * @since 2019-04-20 */@SpringBootApplication@EnableSchedulingpublic class ScheduleTaskApplication {    public static void main(String[] args) {        SpringApplication.run(ScheduleTaskApplication.class, args);    }}